import React, { Component } from "react";
import axios from "axios";
import CurrentActionBar from "./CurrentActionBar";
const titleStyle = {
  fontSize: "180%",
  textAlign: "center"
};
const cardTitle = {
  width: "90%",
  height: "100px",
  backgroundColor: "#eee",
  boxShadow: "5px 5px #fffddd",
  margin: "auto"
};
class Question extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: null,
      answers: [],
      question_id: props.match.params.question_id
    };
  }
  componentDidMount() {
    const questionAnsURL =
      "https://api.stackexchange.com/2.2/questions/20721777/answers?order=desc&sort=activity&site=stackoverflow";
    const questionURL =
      "https://api.stackexchange.com/2.2/questions/20721777?order=desc&sort=activity&site=stackoverflow";

    axios
      .get(`${questionURL}`)
      .then(res => this.setState({ title: res.data.items[0].title }))
      .then(
        axios
          .get(`${questionAnsURL}`)
          .then(res => this.setState({ answers: res.data.items }))
      );
  }
  render() {
    console.log(this.state);
    return (
      <div>
        <div>
          <CurrentActionBar currentAction="Question Title" />
        </div>
        <div style={cardTitle}>
          <p style={titleStyle}>{this.state.title}</p>
        </div>
      </div>
    );
  }
}

export default Question;
